"use strict";const burger=document.getElementById("burger"),navbar=document.querySelector(".navbar__menu__list"),toggle=document.querySelectorAll(".navbar__button__burger-toggle");function showMenu(){toggle.forEach(e=>{"1"===e.style.opacity?(e.style.opacity="0",navbar.classList.remove("active")):(e.style.opacity="1",navbar.classList.add("active"))})}window.addEventListener("click",e=>{e=e.target;burger.contains(e)?showMenu():navbar.contains(e)||(navbar.classList.remove("active"),toggle[1].style.opacity="0",toggle[0].style.opacity="1")});let numSepartor=document.querySelectorAll(".num-separator");numSepartor.forEach(e=>{e.innerText=Number(e.innerText).toLocaleString("en")});